import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DashboardAdmin = () => {
  const [stats, setStats] = useState({ users: 0, stores: 0, ratings: 0 });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await axios.get('/api/admin/stats');
        setStats(res.data);
      } catch (err) {
        console.error('Failed to fetch stats:', err);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Admin Dashboard</h1>
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        <div className="bg-white shadow rounded p-4">
          <p className="text-gray-600">Total Users</p>
          <p className="text-xl font-bold">{stats.users}</p>
        </div>
        <div className="bg-white shadow rounded p-4">
          <p className="text-gray-600">Total Stores</p>
          <p className="text-xl font-bold">{stats.stores}</p>
        </div>
        <div className="bg-white shadow rounded p-4">
          <p className="text-gray-600">Total Ratings</p>
          <p className="text-xl font-bold">{stats.ratings}</p>
        </div>
      </div>
    </div>
  );
};

export default DashboardAdmin;
