import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { getStores, submitRating, updateRating } from '../services/storeService';

const StoreList = () => {
  const { user } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [ratings, setRatings] = useState({});

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await getStores(user.token);
        setStores(response);
      } catch (err) {
        console.error('Failed to fetch stores:', err);
      }
    };
    fetchStores();
  }, [user]);

  const handleRatingChange = (storeId, value) => {
    setRatings({ ...ratings, [storeId]: value });
  };

  const handleRatingSubmit = async (storeId) => {
    try {
      const ratingValue = parseInt(ratings[storeId], 10);
      if (!ratingValue || ratingValue < 1 || ratingValue > 5) return alert('Invalid rating');

      await submitRating(storeId, ratingValue, user.token);
      alert('Rating submitted');
    } catch (err) {
      console.error('Submit failed:', err);
      alert('Rating submit failed');
    }
  };

  const filteredStores = stores.filter(store =>
    store.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    store.address.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Store Listings</h2>
      <input
        type="text"
        placeholder="Search by Name or Address"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="border p-2 w-full mb-4"
      />
      {filteredStores.map(store => (
        <div key={store.id} className="border rounded p-4 mb-4 shadow">
          <h3 className="text-xl font-semibold">{store.name}</h3>
          <p>üìç {store.address}</p>
          <p>‚≠ê Average Rating: {store.avgRating?.toFixed(1) || 'No ratings yet'}</p>
          <input
            type="number"
            min="1"
            max="5"
            placeholder="Rate (1-5)"
            value={ratings[store.id] || ''}
            onChange={(e) => handleRatingChange(store.id, e.target.value)}
            className="border px-2 py-1 mt-2"
          />
          <button
            onClick={() => handleRatingSubmit(store.id)}
            className="ml-2 bg-blue-500 text-white px-3 py-1 rounded"
          >
            Submit Rating
          </button>
        </div>
      ))}
    </div>
  );
};

export default StoreList;
