import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { getStores, submitRating, updateRating } from '../services/storeService';

const StoreList = () => {
  const { user } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [ratings, setRatings] = useState({});
  const [userRatings, setUserRatings] = useState({});

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await getStores(user.token);
        setStores(response);

        // Pre-fill user's submitted ratings if available
        const userRatingsMap = {};
        response.forEach(store => {
          if (store.userRating) {
            userRatingsMap[store.id] = store.userRating;
          }
        });
        setRatings(userRatingsMap);
        setUserRatings(userRatingsMap);
      } catch (err) {
        console.error('Failed to fetch stores:', err);
      }
    };
    fetchStores();
  }, [user]);

  const handleRatingChange = (storeId, value) => {
    setRatings(prev => ({ ...prev, [storeId]: value }));
  };

  const handleRatingSubmit = async (storeId) => {
    const ratingValue = parseInt(ratings[storeId], 10);
    if (!ratingValue || ratingValue < 1 || ratingValue > 5) {
      return alert('Please enter a valid rating between 1 and 5');
    }

    try {
      if (userRatings[storeId]) {
        // User has already submitted a rating — update it
        await updateRating(storeId, ratingValue, user.token);
        alert('Rating updated successfully');
      } else {
        // First-time submission
        await submitRating(storeId, ratingValue, user.token);
        alert('Rating submitted successfully');
      }

      // Update local tracking
      setUserRatings(prev => ({ ...prev, [storeId]: ratingValue }));
    } catch (err) {
      console.error('Rating operation failed:', err);
      alert('Rating failed. Please try again.');
    }
  };

  const filteredStores = stores.filter(store =>
    store.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    store.address.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Store Listings</h2>
      <input
        type="text"
        placeholder="Search by Name or Address"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="border p-2 w-full mb-4"
      />
      {filteredStores.map(store => (
        <div key={store.id} className="border rounded p-4 mb-4 shadow">
          <h3 className="text-xl font-semibold">{store.name}</h3>
          <p>📍 {store.address}</p>
          <p>⭐ Average Rating: {store.avgRating?.toFixed(1) || 'No ratings yet'}</p>
          <p>🧑‍💻 Your Rating: {userRatings[store.id] || 'N/A'}</p>
          <input
            type="number"
            min="1"
            max="5"
            placeholder="Rate (1–5)"
            value={ratings[store.id] || ''}
            onChange={(e) => handleRatingChange(store.id, e.target.value)}
            className="border px-2 py-1 mt-2"
          />
          <button
            onClick={() => handleRatingSubmit(store.id)}
            className="ml-2 bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
          >
            {userRatings[store.id] ? 'Update Rating' : 'Submit Rating'}
          </button>
        </div>
      ))}
    </div>
  );
};

export default StoreList;
