import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../contexts/AuthContext';

const DashboardOwner = () => {
  const { user } = useContext(AuthContext);
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/owner/dashboard', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setData(res.data);
      } catch (err) {
        console.error('Error fetching owner data:', err);
      }
    };

    fetchDashboardData();
  }, []);

  if (!data) return <p className="p-6">Loading dashboard...</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">üè™ {data.storeName} Dashboard</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-6">
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-gray-600">‚≠ê Average Rating</h2>
          <p className="text-2xl font-bold">{data.averageRating}</p>
        </div>
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-gray-600">üßæ Total Ratings Received</h2>
          <p className="text-2xl font-bold">{data.totalRatings}</p>
        </div>
      </div>

      <h3 className="text-xl font-semibold mb-3">üìã Ratings Received:</h3>
      <ul className="space-y-3">
        {data.ratings.map((r, idx) => (
          <li key={idx} className="border rounded p-3 bg-gray-50">
            <p><strong>{r.user}</strong> rated <strong>{r.rating}/5</strong></p>
            <p className="text-sm text-gray-500">{new Date(r.createdAt).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DashboardOwner;
